import React from 'react';
import { Card } from 'react-bootstrap';
import styled from 'styled-components';

const StyledDiv = styled.div`
  margin-bottom: 10px;
`;

const PostCard = ({ post }) => {
  return (
    <StyledDiv>
      <Card id={post.id}>
        <Card.Header>글 제목 : {post.title}</Card.Header>
        <Card.Body>
          <blockquote className="blockquote mb-0">
            <p> {post.content} </p>
            <footer className="blockquote-footer">
              {post.author} <cite title="Source Title">{post.created}</cite>
            </footer>
          </blockquote>
        </Card.Body>
      </Card>
    </StyledDiv>
  );
};

export default PostCard;
