import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Container } from 'react-bootstrap';
import PostCard from '../components/PostCard';

const ListPage = () => {
  const [posts, setPosts] = useState([
    {
      id: 1,
      title: 'content1',
      content: 'aaaaaaaaaa',
      author: 'a',
      created: '2020.01.20',
    },
    {
      id: 2,
      title: 'content2',
      content: 'bbbbbbb',
      author: 'b',
      created: '2020.01.20',
    },
    {
      id: 3,
      title: 'content3',
      content: 'ccccccccccccc',
      author: 'c',
      created: '2020.01.20',
    },
    {
      id: 4,
      title: 'content4',
      content: 'dddddd',
      author: 'd',
      created: '2020.01.20',
    },
  ]);

  const getPosts = async () => {
    let posts = await axios.get('http://localhost:8888/post/');
    console.log(posts);
  };

  useEffect(() => {
    getPosts();
  }, []);
  return (
    <Container>
      <h1>리스트 페이지</h1>
      <br />
      {isLoading ? 'Loading...' : posts.map((post) => <PostCard post={post} />)}
      <br />
    </Container>
  );
};

export default ListPage;
