import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Card, Container } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const DetailPage = ({ match }) => {
  //console.log(match);
  const id = match.params.id;
  const [onePost, setOnePost] = useState([]);

  useEffect(() => {
    axios({
      method: 'GET',
      url: `http://localhost:8888/post/${id}`,
    })
      .then((res) => {
        //console.log(res);
        setOnePost(res.data.data);
      })
      .catch((error) => {});
  }, []);

  return (
    <Container>
      <Card id={onePost.id} style={{ width: '800px', height: '600px' }}>
        <Card.Body>
          <Card.Title>{onePost.title}</Card.Title>
          <Card.Subtitle className="mb-2 text-muted">
            {onePost.author}
          </Card.Subtitle>
          <Card.Text>{onePost.content}</Card.Text>
          <Link
            to={{
              pathname: `/post/${onePost.id}/update`,
              state: { onePost },
            }}
          >
            수정하기
          </Link>
          <Link to="/">삭제하기</Link>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default DetailPage;
