import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Button, Card, Container } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const DetailPage = ({ match, history }) => {
  //console.log(match);
  const id = match.params.id;
  const [onePost, setOnePost] = useState([]);

  useEffect(() => {
    axios({
      method: 'GET',
      url: 'http://localhost:8888/post/' + id,
    })
      .then((res) => {
        //console.log(res);
        setOnePost(res.data.data);
      })
      .catch((error) => {});
  }, []);

  const deletePost = () => {
    axios({
      method: 'DELETE',
      url: 'http://localhost:8888/post/' + id,
    })
      .then((res) => {
        console.log(res);
        if (res.data.code === 1) {
          history.push('/');
        } else {
          alert('삭제 실패');
        }
      })
      .catch((error) => {});
  };

  return (
    <Container>
      <Card id={onePost.id} style={{ width: '800px', height: '600px' }}>
        <Card.Body>
          <Card.Title>{onePost.title}</Card.Title>
          <Card.Subtitle className="mb-2 text-muted">
            {onePost.author}
          </Card.Subtitle>
          <Card.Text>{onePost.content}</Card.Text>
          <Link variant="primary">수정하기</Link>
          {'  '}
          <Button variant="danger" onClick={deletePost}>
            삭제하기
          </Button>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default DetailPage;
