import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Card } from 'react-bootstrap';

const DetailPage = ({ match }) => {
  console.log(match);
  const id = match.params.id;
  const [onePost, setOnePost] = useState([]);

  useEffect(() => {
    axios({
      method: 'GET',
      url: `http://localhost:8888/post/${id}`,
    })
      .then((res) => {
        console.log(res);
        setOnePost(res.data.data);
      })
      .catch((error) => {});
  }, []);

  return (
    <Card id={onePost.id} style={{ width: '18rem' }}>
      <Card.Body>
        <Card.Title>{onePost.title}</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">
          {onePost.author}
        </Card.Subtitle>
        <Card.Text>{onePost.content}</Card.Text>
        <Card.Link href="#">수정하기</Card.Link>
        <Card.Link href="#">삭제하기</Card.Link>
      </Card.Body>
    </Card>
  );
};

export default DetailPage;
